import React from 'react';
import { Link } from "react-router-dom";
import '../css/Navigation.css';

class Navigation extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      style: "link"
    }
    this.handleHover = this.handleHover.bind(this);
  }
  handleHover() {
    var {style} = this.state;
    this.setState({style: "hover"})
  }
  render() {
    return (
      <div>
        <Link to={`/home`}>
          <button className={this.state.style} onMouseOver={this.handleHover} onMouseLeave={this.handleHover}>
            Home
          </button>
        </Link>
        <Link to={`/about`}>
          <button className="link">
            About
          </button>
        </Link>
        <Link to={`/portfolio`}>
          <button className="link">
            Portfolio
          </button>
        </Link>
        <Link to={`/resume`}>
          <button className="link">
            Resume
          </button>
        </Link>
        <Link to={`/contact`}>
          <button className="link">
            Contact
          </button>
        </Link>
      </div>
  )
} 
}

export default Navigation;
