              <Route path="/contact" component={Contact}></Route>
            </Router> */}
          </div>
        </div>    
      )
    }
  }
  componentDidMount() {
  }
  render() {
      return (
        <div>
            {this.loader()}
        </div>
      )
    }


}

export default App;
