              <span></span>
              <span></span>
              <span></span>
              <span></span>
              {"Contact"}
            </a>
            <span/>
          </div>

          <Router>
            <Route path="/" component={Main}></Route>
            <Route path="/about" component={About}></Route>
            <Route path="/portfolio" component={Portfolio}></Route>
            <Route path="/contact" component={Contact}></Route>
