      <Provider store={store}>
        <Menu className="menu" />
        <h1 className="welcome">Welcome</h1>
        <Route exact path="http://stevenyoh.com/home" component={Home} />
        <Route exact path="http://stevenyoh.com/about" component={About} />
        <Route exact path="http://stevenyoh.com/portfolio" component={Portfolio} />
